import {
  Box,
  Text,
  ScrollView,
  HStack,
  View,
  Pressable,
  Image,
} from "native-base";
import React, { useState } from "react";
import Colors from "../color";
import { Ionicons } from "@expo/vector-icons";
import HomeProducts from "../Components/HomeProducts";

function ProfileScreen2({ navigation }) {
  const [value, setValue] = useState(0);
  return (
    <Box safeAreaTop flex={1} bg={Colors.white}>
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Thông tin cá nhân
        </Text>
      </HStack>
      <ScrollView px={3} showsVerticalScrollIndicator={false} py={3}>
        <View flexDirection="row" >
          <Image
            source={{
              uri: "https://img.lovepik.com/free-png/20210923/lovepik-t-shirt-png-image_401190055_wh1200.png",
            }}
            h={19}
            w={19}
            alt="avatar"
            rounded="full"
          />
          <Text>Tên</Text>
        </View>
      </ScrollView>
    </Box>
  );
}

export default ProfileScreen2;
