import React, { useEffect } from "react";
import { View, Text, TouchableOpacity } from "react-native";
import { Image, Box } from "native-base";
import { MaterialIcons, Feather } from "@expo/vector-icons";
import CustomButton from "../Components/CustomButton";
import InputField from "../Components/InputField";
import Colors from "../color";
const URL = `http://192.168.1.7:5000/`;
function LoginScreen2({ navigation }) {
  const click = () => {
    fetch(`${URL}/`)
      .then((res) => res.json())
      .then(async(res)=>{
        console.log("Clicked");
      })
      .catch((err) => console.log("error", err));
  };
  return (
    <Box
      safeAreaTop
      style={{
        flex: 1,
        justifyContent: "center",
        backgroundColor: Colors.white,
      }}
    >
      <View style={{ paddingHorizontal: 25 }}>
        <View style={{ alignItems: "center" }}>
          <Image
            source={{
              uri: "https://static.chotot.com/storage/assets/LOGIN/logo_register.png",
            }}
            alt="twiter"
            height={200}
            width={200}
          />
        </View>

        <Text
          style={{
            fontSize: 28,
            fontWeight: "500",
            color: "#333",
            marginBottom: 30,
          }}
        ></Text>

        <InputField
          label="Email ID"
          icon={
            <MaterialIcons
              name="email"
              size={20}
              color="#666"
              style={{ marginRight: 15 }}
            />
          }
          keyboardType="email-address"
        />

        <InputField
          label="Password"
          icon={
            <Feather
              name="lock"
              size={20}
              color="#666"
              style={{ marginRight: 15 }}
            />
          }
          inputType="password"
          fieldButtonLabel="Forgot?"
          fieldButtonFunction={() => {}}
        />

        {/* <CustomButton
          label={"Login"}
          onPress={() => navigation.navigate("Bottom")}
        /> */}
        <CustomButton
          label={"Login"}
          onPress={()=>click()}
        />
        <Text style={{ textAlign: "center", color: "#666", marginBottom: 30 }}>
          Or, login with ...
        </Text>

        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            marginBottom: 30,
          }}
        >
          <TouchableOpacity
            onPress={() => {}}
            style={{
              borderColor: "#ddd",
              borderWidth: 2,
              borderRadius: 10,
              paddingHorizontal: 30,
              paddingVertical: 10,
            }}
          >
            <Image
              source={require("../../assets/google.png")}
              alt="google"
              height={10}
              width={10}
            />
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => {}}
            style={{
              borderColor: "#ddd",
              borderWidth: 2,
              borderRadius: 10,
              paddingHorizontal: 30,
              paddingVertical: 10,
            }}
          >
            <Image
              source={require("../../assets/facebook.png")}
              alt="fb"
              height={10}
              width={10}
            />
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => {}}
            style={{
              borderColor: "#ddd",
              borderWidth: 2,
              borderRadius: 10,
              paddingHorizontal: 30,
              paddingVertical: 10,
            }}
          >
            {/* <TwitterSVG height={24} width={24} /> */}
            <Image
              source={require("../../assets/twiter.png")}
              alt="twiter"
              height={10}
              width={10}
            />
          </TouchableOpacity>
        </View>

        <View
          style={{
            flexDirection: "row",
            justifyContent: "center",
            marginBottom: 30,
          }}
        >
          <Text>New to the app? </Text>
          <TouchableOpacity onPress={() => navigation.navigate("Register")}>
            <Text style={{ color: "#4169E1", fontWeight: "700" }}>
              Register
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    </Box>
  );
}

export default LoginScreen2;
