import {
  Box,
  Text,
  ScrollView,
  HStack,
  Pressable,
  View,
  Image,
  VStack,
  Input,
} from "native-base";
import React from "react";
import Colors from "../color";
import { Ionicons } from "@expo/vector-icons";
import { TouchableOpacity } from "react-native";

function CreateBlogScreen({ navigation }) {
  return (
    <Box safeAreaTop bg={Colors.white}>
      {/* Header */}
      <View space={3} w="full" px={4} bg="#FFA500" py={3} alignItems="center">
        <Text bold fontSize={17} color="white" textAlign="center">
          Đăng tin
        </Text>
      </View>
      {/* Body */}
      <ScrollView showsVerticalScrollIndicator={false} py={3} px={3}>
        <View
          flexDirection="row"
          bg={Colors.white}
          w="full"
          px={3}
          alignItems="center"
        >
          <Text bold>CHỌN DANH MỤC</Text>
        </View>
        <Box mt={5}>
          <Pressable
            px={3}
            h={10}
            borderTopWidth={0.5}
            borderTopColor="gray.400"
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
          >
            <HStack justifyContent="space-between" alignItems="center">
              <Text>Xe cộ</Text>
              <Ionicons
                name="chevron-forward"
                size={28}
                color={Colors.deepestGray}
                style={{ alignSelf: "center" }}
              />
            </HStack>
          </Pressable>
          <Pressable
            px={3}
            mt={3}
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
          >
            <HStack justifyContent="space-between">
              <Text>Đồ điện tử</Text>
              <Ionicons
                name="chevron-forward"
                size={28}
                color={Colors.deepestGray}
                style={{ alignSelf: "center" }}
              />
            </HStack>
          </Pressable>
          <Pressable
            px={3}
            mt={3}
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
          >
            <HStack justifyContent="space-between">
              <Text>Thời trang, đồ dùng cá nhân</Text>
              <Ionicons
                name="chevron-forward"
                size={28}
                color={Colors.deepestGray}
                style={{ alignSelf: "center" }}
              />
            </HStack>
          </Pressable>
          <Pressable
            px={3}
            mt={3}
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
          >
            <HStack justifyContent="space-between">
              <Text>Giải trí, thể thao</Text>
              <Ionicons
                name="chevron-forward"
                size={28}
                color={Colors.deepestGray}
                style={{ alignSelf: "center" }}
              />
            </HStack>
          </Pressable>
          <Pressable
            px={3}
            mt={3}
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
          >
            <HStack justifyContent="space-between">
              <Text>Tủ lạnh, máy giặt,..</Text>
              <Ionicons
                name="chevron-forward"
                size={28}
                color={Colors.deepestGray}
                style={{ alignSelf: "center" }}
              />
            </HStack>
          </Pressable>
          <Pressable
            px={3}
            mt={3}
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
          >
            <HStack justifyContent="space-between">
              <Text>Đồ gia dụng, nội thất</Text>
              <Ionicons
                name="chevron-forward"
                size={28}
                color={Colors.deepestGray}
                style={{ alignSelf: "center" }}
              />
            </HStack>
          </Pressable>
        </Box>
      </ScrollView>
    </Box>
  );
}

export default CreateBlogScreen;
