import React from "react";
import { ScrollView, Image, View, VStack } from "native-base";

const imageSlide = [
  "https://img.freepik.com/premium-vector/modern-sale-banner-website-slider-template-design_54925-45.jpg?w=2000",
  "https://sastastoreonline.com/wp-content/uploads/2020/06/slider-3-1.jpg",
];

function SlideScreen() {
  return (
    <VStack flex={1} h={50}>
      <ScrollView //onScroll={({nativeEvent}) => onchange(nativeEvent)}
        showsHorizontalScrollIndicator={false}
        pagingEnabled
        horizontal
      >
        {/* {imageSlide.map((e, index) => (
          <Image
            key={index}
            resizeMode="contain"
            alt="slide"
            source={{ uri: e }}
            w="full"
          />
        ))} */}
        <Image
            resizeMode="contain"
            alt="slide"
            source={{ uri: "https://img.freepik.com/premium-vector/modern-sale-banner-website-slider-template-design_54925-45.jpg?w=2000"}}
            w="full"
            h="full"
          />
      </ScrollView>
    </VStack>
  );
}

export default SlideScreen;
