import React from 'react';
import { Center, Text } from 'native-base';

function OrderItem() {
  return (
    <Center bg={bg} p={4} rounded={5}>
        <Text color={color} fontSize={size} bold={bold}>
            {children}
        </Text>
    </Center>
  );
}

export default OrderItem;
