import { NativeBaseProvider, StatusBar, View } from "native-base";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import BottomNav from "./src/Navigations/BottomNav";
import LoginScreen2 from "./src/Screens/LoginScreen2";
import RegisterScreen2 from "./src/Screens/RegisterScreen2";
import SimiliarProductScreen from "./src/Screens/SimiliarProductScreen";
import UpdateProfileScreen from "./src/Screens/UpdateProfileScreen";
import React, { useEffect, useMemo, useState } from "react";
import { ActivityIndicator } from "react-native";
import { AuthContext } from "./src/Components/AuthContext";
import axios from "axios";

const Stack = createNativeStackNavigator();

export default function App() {
  // const [isLoading, setIsLoading] = useState(true);
  // const [userToken, setUserToken] = useState(null);

  const initialLoginState = {
    isLoading: true,
    userToken: null,
  };

  const loginReducer = (prevState, action) => {
    switch (action.type) {
      case "RETRIEVE_TOKEN":
        return {
          ...prevState,
          isLoading: false,
        };
      case "LOGIN":
        return {
          ...prevState,
          userToken: action.token,
          isLoading: false,
        };
      case "LOGOUT":
        return {
          ...prevState,
          userToken: null,
          isLoading: false,
        };
      case "SIGNUP":
        return {
          ...prevState,
          userToken: action.token,
          isLoading: false,
        };
    }
  };

  const [loginState, dispatch] = React.useReducer(
    loginReducer,
    initialLoginState
  );

  const authContext = useMemo(
    () => ({
      logIn: (email, password) => {
        let userToken;
        userToken = null;
        const config = { headers: { "Content-Type": "application/json" } };
        const login = async () => {
          try {
            const { data } = await axios.post(
              `http://192.168.1.7:5000/api/user/login`,
              { email, password },
              config
            );
            dispatch({ type: "LOGIN", token: data._id });
            await AsyncStorage.setItem("userID", data._id);
          } catch (error) {
            console.log(error);
          }
        };
        login();
      },
      logOut: () => {
        setUserToken(null);
        setIsLoading(false);
      },
      signUp: () => {
        setUserToken("adwawdada");
        setIsLoading(false);
      },
    }),
    []
  );

  useEffect(() => {
    setTimeout(() => {
      dispatch({ type: "RETRIEVE_TOKEN" });
    }, 1000);
  }, []);

  if (loginState.isLoading) {
    return (
      <NativeBaseProvider>
        <View flex={1} justifyContent="center" alignItems="center">
          <ActivityIndicator size="large" />
        </View>
      </NativeBaseProvider>
    );
  }

  return (
    <AuthContext.Provider value={authContext}>
      <NativeBaseProvider>
        <NavigationContainer>
          <StatusBar hidden={true} />
          {loginState.userToken != null ? (
            <Stack.Navigator
              initialRouteName="Bottom"
              screenOptions={{
                headerShown: false,
              }}
            >
              <Stack.Screen name="Login" component={LoginScreen2} />
              <Stack.Screen name="Register" component={RegisterScreen2} />
              <Stack.Screen name="Bottom" component={BottomNav} />
              <Stack.Screen
                name="SimiliarProduct"
                component={SimiliarProductScreen}
              />
              <Stack.Screen
                name="UpdateProfile"
                component={UpdateProfileScreen}
              />
            </Stack.Navigator>
          ) : (
            <LoginScreen2 />
          )}
        </NavigationContainer>
      </NativeBaseProvider>
    </AuthContext.Provider>
  );
}
