import React from 'react';
import { Box, CheckIcon, FormControl, Heading,Select,Text, VStack } from 'native-base';
import Colors from '../color';
import Rating from './Rating';
import Message from './Notfications/Message';
import StarRating from 'react-native-star-rating';

function Review() {
  return (
    <Box my={9}>
        <Heading bold fontSize={15} mb={2}>
            Review
        </Heading>
        {/* IF NO REVIEW */}
        <Message color={Colors.main} bg={Colors.deepGray} bold
            children={"No review"} />
        {/* REVIEW */}
        <Box p={3} bg={Colors.deepGray} mt={5} rounded={5}>
            <Heading fontSize={15} color={Colors.black}>
                User doe
            </Heading>
            <Rating value={4}/>
            <Text my={2} fontSize={11}>Jan 12 20022</Text> 
            <Message color={Colors.black} bg={Colors.white} size={10} 
                children={"Dep lamDep lamDep lamDep lamDep lamDep lamDep lamDep lamDep lam"} />
                
        </Box>
        {/* WRITE REVIEW */}
        <Box mt={6}>
            <Heading fontSize={15} bold mb={4}>
                REVIEW THIS PRODUCT
            </Heading>
            <VStack space={6}>
                <FormControl>
                    <FormControl.Label _text={{
                        fontSize: "12px",
                        fontWeight: "bold",
                    }}>Rating</FormControl.Label>
                    <Select bg={Colors.subGreen} borderWidth={0} rounded={5} py={3} placeholder="Choose rate"
                    _selectedItem={{
                        bg: Colors.subGreen,
                        endIcon: <CheckIcon size={2}/>
                    }}>
                        <Select.Item label='1 - Poor'>
                        
                        </Select.Item>
                    </Select>
                    <StarRating/>
                </FormControl>
            </VStack>
        </Box>
    </Box>
  );
}

export default Review;
