import React from "react";
import {
  ScrollView,
  Text,
  Box,
  Center,
  VStack,
  Image,
  HStack,
} from "native-base";
import Colors from "../color";
import Buttone from "../Components/Buttone";

const Inputs = [
  {
    Label: "NAME",
    type: "text",
  },
  {
    Label: "PHONE",
    type: "email",
  },
  {
    Label: "CITY",
    type: "text",
  },
  {
    Label: "COUNTRY",
    type: "text",
  },
  {
    Label: "ADDRESS",
    type: "text",
  },
];

function PaymentScreen() {
  return (
    <Box flex={1} safeArea bg={Colors.main} py={5}>
      <Center pb={15}>
        <Text color={Colors.white} fontSize={14} bold>
          PAYMENT METHOD
        </Text>
      </Center>
      <Box h="full" bg={Colors.subGreen} px={5}>
        <ScrollView showsVerticalScrollIndicator={false}>
          <VStack space={6} mt={5} pb={10}>
            <HStack
              alignItems="center"
              bg={Colors.white}
              px={3}
              py={1}
              rounded={10}
            >
              <Box>
                <Image source={{uri:"https://upload.wikimedia.org/wikipedia/commons/a/a4/Paypal_2014_logo.png"}} alt="paypal" resis></Image>
              </Box>
            </HStack>
            <Buttone bg={Colors.main} color={Colors.white} mt={5}>
              CONTINUE
            </Buttone>
          </VStack>
        </ScrollView>
      </Box>
    </Box>
  );
}

export default PaymentScreen;
