import React from "react";
import { ScrollView, Image, View, HStack } from "native-base";

const imageSlide = [
  "https://img.freepik.com/premium-vector/modern-sale-banner-website-slider-template-design_54925-45.jpg?w=2000",
  "https://sastastoreonline.com/wp-content/uploads/2020/06/slider-3-1.jpg",
];

function SlideScreen() {
  return (
    <View flex={1}>
      {/* <ScrollView //onScroll={({nativeEvent}) => onchange(nativeEvent)}
        showsHorizontalScrollIndicator={false}
        horizontal
      > */}
        {imageSlide.map((e, index) => (
          <Image
            key={index}
            resizeMode="contain"
            alt="slide"
            source={{ uri: e }}
            w="full"
            h={200}
          />
        ))}
        <HStack>
          <Image
            resizeMode="contain"
            alt="slide"
            source={{
              uri: "https://img.freepik.com/premium-vector/modern-sale-banner-website-slider-template-design_54925-45.jpg?w=2000",
            }}
            w="full"
            h={200}
          />
          <Image
            resizeMode="contain"
            alt="slide"
            source={{
              uri: "https://img.freepik.com/premium-vector/modern-sale-banner-website-slider-template-design_54925-45.jpg?w=2000",
            }}
          />
        </HStack>
      {/* </ScrollView> */}
    </View>
  );
}

export default SlideScreen;
