import {
  Box,
  Text,
  ScrollView,
  HStack,
  Pressable,
  View,
  Image,
  VStack,
  Input,
  FormControl,
  TextArea,
  Button,
} from "native-base";
import React, { useEffect, useState } from "react";
import Colors from "../color";
import { Ionicons, Entypo } from "@expo/vector-icons";
import { AirbnbRating } from "react-native-ratings";
import axios from "axios";
const Url = `http://192.168.1.7:5000`;
function ReviewScreen({ navigation, route }) {
  const id = route.params;
  const [owner, setOwner] = useState("");
  const fetchProfileOwner = async () => {
    try {
      const { data } = await axios.get(`${Url}/api/users/profile/${id}`);
      setOwner(data);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    fetchProfileOwner();
  }, []);
  return (
    <Box safeAreaTop bg={Colors.white}>
      {/* Header */}
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Đánh giá
        </Text>
      </HStack>
      {/* Body */}
      <View px={5}>
        <Text fontSize={17} bold textAlign="center">
          Trải nghiệm của bạn với {owner.name} như thế nào
        </Text>
        <VStack space={6}>
          <FormControl>
            <AirbnbRating
              reviewSize={12}
              size={50}
              reviews={""}
              defaultRating={0}
            />
          </FormControl>
          <FormControl>
            <FormControl.Label
              _text={{
                fontSize: "12px",
                fontWeight: "bold",
              }}
            >
              Hãy viết đánh giá của bạn
            </FormControl.Label>
            <TextArea
              height={40}
              mt={2}
              placeholder="Chia sẻ thêm về trải nghiệm mua bán của bạn"
              alignItems="stretch"
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
          </FormControl>
          <Button bg="#EE9A00" color={Colors.white}>
            <Text>ĐÁNH GIÁ</Text>
          </Button>
        </VStack>
      </View>
    </Box>
  );
}

export default ReviewScreen;
