import {
  Box,
  Text,
  ScrollView,
  HStack,
  Pressable,
  View,
  Image,
  VStack,
  Input,
  FormControl,
  TextArea,
} from "native-base";
import React, { useState } from "react";
import Colors from "../color";
import { Ionicons, Entypo } from "@expo/vector-icons";
import * as ImagePicker from "expo-image-picker";
import { StorageClient } from "@supabase/storage-js";
import { useDispatch } from "react-redux";
import { updateAvatar } from "../Redux/Actions/UserActions";
import { ToastAndroid } from "react-native";
import { AirbnbRating } from "react-native-ratings";
const STORAGE_URL = "https://ngfxbagkjhxyputmwoxp.supabase.co/storage/v1";
const SERVICE_KEY =
  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Im5nZnhiYWdramh4eXB1dG13b3hwIiwicm9sZSI6InNlcnZpY2Vfcm9sZSIsImlhdCI6MTY3MDIyMDE2NSwiZXhwIjoxOTg1Nzk2MTY1fQ.v1Bml2eA_eQno7gMBfDbtgLiyovkVs9orOz1w_89aVI"; //! service key, not anon key

const storageClient = new StorageClient(STORAGE_URL, {
  apikey: SERVICE_KEY,
  Authorization: `Bearer ${SERVICE_KEY}`,
});
function ReviewScreen({ navigation, route }) {
  const userInfo = route.params;
  const seller = route.params;
  console.log(seller);
  return (
    <Box safeAreaTop bg={Colors.white}>
      {/* Header */}
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Đánh giá
        </Text>
      </HStack>
      {/* Body */}
      <View>
        <Text fontSize={15} bold mb={4}>
          Trải nghiệm của bạn với như thế nào
        </Text>
        <VStack space={6}>
          <FormControl>
            <FormControl.Label
              _text={{
                fontSize: "12px",
                fontWeight: "bold",
              }}
            >
              Rating
            </FormControl.Label>

            <AirbnbRating reviewSize={12} size={25} defaultRating={0} />
          </FormControl>
          <FormControl>
            <FormControl.Label
              _text={{
                fontSize: "12px",
                fontWeight: "bold",
              }}
            >
              Comment
            </FormControl.Label>
            <TextArea
              h={24}
              w="full"
              placeholder="This product........."
              borderWidth={0}
              bg="#F8F8FF"
              py={4}
              _focus={{ bg: "#F8F8FF" }}
            ></TextArea>
          </FormControl>
          <Pressable bg="#EE9A00" color={Colors.white}>
            <Text>ĐÁNH GIÁ</Text>
          </Pressable>
        </VStack>
      </View>
    </Box>
  );
}

export default ReviewScreen;
