import React, { useEffect, useState } from "react";
import {
  Box,
  HStack,
  Image,
  Pressable,
  ScrollView,
  Text,
  View,
} from "native-base";
import Colors from "../../color";
import { Ionicons, Feather } from "@expo/vector-icons";
import { Storage } from "expo-storage";

function Showing() {
  const [product, setProduct] = useState([]);
  const getProduct = async () => {
    const productList = await Storage.getItem({ key: "productList" });
    setProduct(JSON.parse(productList));
  };
  useEffect(() => {
    getProduct();
  }, []);
  return (
    <Box h="full" bg={Colors.gray}>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View mt={3} bg={Colors.white} flexDirection="row" alignItems="center">
          <View w="30%" borderRightWidth={0.5} borderRightColor="gray.400">
            <Image
              source={{
                uri: "https://thoitrangbigsize.vn/wp-content/uploads/2019/09/1-6.jpg",
              }}
              alt="img"
              h={20}
              resizeMode="contain"
            />
          </View>

          <View ml={2} w="40%">
            <Text isTruncated>Áo khoác xịn đẹp lắm haha</Text>
            <Text bold color={Colors.red}>
              100,000đ
            </Text>
            <Text color="gray.500" fontSize={11}>
              23/12/2022 15:09
            </Text>
          </View>
        </View>
        <View
          flexDirection="row"
          flex={1}
          justifyContent="space-between"
          bg={Colors.white}
          mb={3}
        >
          <Pressable
            w="50%"
            borderWidth={0.6}
            borderColor="gray.300"
            alignItems="center"
            p={1}
          >
            <HStack space={2}>
              <Ionicons name="ios-eye-off-outline" size={24} color="#3366FF" />
              <Text color="#3366FF">Đã bán/Ẩn tin</Text>
            </HStack>
          </Pressable>
          <Pressable
            w="50%"
            borderWidth={0.6}
            borderColor="gray.300"
            alignItems="center"
            p={1}
          >
            <HStack space={2}>
              <Feather name="edit" size={24} color="#3366FF" />
              <Text color="#3366FF">Chỉnh sửa</Text>
            </HStack>
          </Pressable>
        </View>

        <View mt={2} bg={Colors.white} flexDirection="row" alignItems="center">
          <View w="30%" borderRightWidth={0.5} borderRightColor="gray.400">
            <Image
              source={{
                uri: "https://thoitrangbigsize.vn/wp-content/uploads/2019/09/1-6.jpg",
              }}
              alt="img"
              h={20}
              resizeMode="contain"
            />
          </View>
          <View ml={2} w="40%">
            <Text isTruncated>Áo khoác xịn đẹp lắm haha</Text>
            <Text bold color={Colors.red}>
              100,000đ
            </Text>
            <Text color="gray.500" fontSize={11}>
              23/12/2022 15:09
            </Text>
          </View>
        </View>
        <View
          flexDirection="row"
          flex={1}
          justifyContent="space-between"
          bg={Colors.white}
          mb={3}
        >
          <Pressable
            w="50%"
            borderWidth={0.6}
            borderColor="gray.300"
            alignItems="center"
            p={1}
          >
            <HStack space={2}>
              <Ionicons name="ios-eye-off-outline" size={24} color="#3366FF" />
              <Text color="#3366FF">Đã bán/Ẩn tin</Text>
            </HStack>
          </Pressable>
          <Pressable
            w="50%"
            borderWidth={0.6}
            borderColor="gray.300"
            alignItems="center"
            p={1}
          >
            <HStack space={2}>
              <Feather name="edit" size={24} color="#3366FF" />
              <Text color="#3366FF">Chỉnh sửa</Text>
            </HStack>
          </Pressable>
        </View>
      </ScrollView>
    </Box>
  );
}

export default Showing;
