import React from "react";
import {
  Box,
  Image,
  Pressable,
  ScrollView,
  Spacer,
  Text,
  View,
} from "native-base";
import Colors from "../../color";

function Reject() {
  return (
    <Box h="full" bg={Colors.gray}>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View mt={2} bg={Colors.white} flexDirection="row" alignItems="center">
          <View w="30%" borderRightWidth={0.5} borderRightColor="gray.400">
            <Image
              source={{
                uri: "https://thoitrangbigsize.vn/wp-content/uploads/2019/09/1-6.jpg",
              }}
              alt="img"
              h={20}
              resizeMode="contain"
            />
          </View>

          <View ml={2} w="40%" >
            <Text numberOfLines={2} isTruncated>Áo khoác xịn đẹp lắm haha</Text>
            <Text bold>100,000đ</Text>
          </View>
          <Spacer />
          <Pressable
            borderWidth={0.8}
            mr={5}
            rounded={10}
            pr={4}
            pl={4}
            pt={1}
            pb={1}
          >
            <Text>Đã bị huỷ</Text>
          </Pressable>
        </View>

        <View mt={2} bg={Colors.white} flexDirection="row" alignItems="center">
          <View w="30%" borderRightWidth={0.5} borderRightColor="gray.400">
            <Image
              source={{
                uri: "https://thoitrangbigsize.vn/wp-content/uploads/2019/09/1-6.jpg",
              }}
              alt="img"
              h={20}
              resizeMode="contain"
            />
          </View>

          <View ml={2} w="40%">
            <Text isTruncated>Áo khoác xịn đẹp lắm haha</Text>
            <Text bold>100,000đ</Text>
          </View>
          <Spacer />
          <Pressable
            borderWidth={0.8}
            mr={5}
            rounded={10}
            pr={4}
            pl={4}
            pt={1}
            pb={1}
          >
            <Text>Đã bị huỷ</Text>
          </Pressable>
        </View>
      </ScrollView>
    </Box>
  );
}

export default Reject;
