import React from 'react';
import { View, Text, Box, Center, ScrollView } from 'native-base';
import { TouchableOpacity } from 'react-native';

function SimiliarProducts() {
  return (
    <View pb={10}>
        <View flexDirection="row" justifyContent="space-between">
            <Text bold>Tin đăng tương tự</Text>
            <TouchableOpacity><Text bold>Xem tất cả {'>'}</Text></TouchableOpacity>
        </View>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
            
        </ScrollView>
    </View>
  );
}

export default SimiliarProducts;
