import React, { useEffect, useState } from "react";
import {
  Text,
  Box,
  ScrollView,
  Image,
  Button,
  Center,
  HStack,
  View,
  Input,
  Select,
  CheckIcon,
} from "native-base";
import HomeSearch from "../Components/HomeSearch";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";
import SlideScreen from "../Components/SlideScreen";
import { ActivityIndicator, RefreshControl } from "react-native";
import axios from "axios";
import { EvilIcons, Ionicons } from "@expo/vector-icons";
import { Storage } from "expo-storage";
const wait = (timeout) => {
  return new Promise((resolve) => setTimeout(resolve, timeout));
};

const categories = [
  "Đồ điện tử",
  "Thời trang, đồ dùng cá nhân",
  "Giải trí thể thao",
];
const cities = [
  "Đà nẵng",
  "Quảng Trị",
  "Quảng Nam",
  "Thừa thiên Huế",
  "Quảng Ngãi",
  "Quảng Bình",
];
const Url = `http://192.168.1.3:5000`;

function HomeScreen() {
  const [pro, setPro] = useState([]);
  const [loading, setLoading] = useState(true);
  const [category, setCategory] = useState("");
  const [city, setCity] = useState("");
  const [search, setSearch] = useState("");

  const fetchProducts = async () => {
    const { data } = await axios.get(`${Url}/api/product/show/accept`);
    if (search.trim() != "" || category.trim() != null) {
      setPro(
        data.filter(
          (data) =>
            data.title
              .toString()
              .toLowerCase()
              .includes(search.toString().toLowerCase()) &&
            data.category.includes(category) &&
            data.city.includes(city)
        )
      );
    } else {
      setPro(data);
    }
  };

  useEffect(() => {
    fetchProducts();
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  }, []);

  const [refreshing, setRefreshing] = React.useState(false);
  const onRefresh = React.useCallback(() => {
    setRefreshing(true);
    setLoading(true);
    fetchProducts();
    wait(1000).then(() => {
      setRefreshing(false);
      setLoading(false);
    });
  }, []);
  return (
    <Box bg={Colors.gray} flex={1}>
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <View
          flex={1}
          flexDirection="row"
          alignItems="center"
          bg={Colors.white}
          rounded={5}
        >
          <EvilIcons
            name="search"
            size={24}
            color="#8B8B7A"
            style={{ marginLeft: 5 }}
          />
          <Input
            placeholder="Tìm kiếm "
            w="85%"
            size={10}
            pl={2}
            bg={Colors.white}
            flex={1}
            type="search"
            h={9}
            borderWidth={0}
            _focus={{ bg: Colors.white }}
            onChangeText={(e) => {
              e = e.replace(/  +/g, " ");
              setSearch(e.replace(/^[ \t]+|[ \t]+$/gm, ""));
            }}
            onEndEditing={() => {
              setLoading(true);
              fetchProducts();
              wait(500).then(() => {
                setLoading(false);
              });
            }}
            variant="filled"
          />
        </View>
        <Ionicons name="options" size={24} color="black" />
      </HStack>

      <ScrollView
        showsVerticalScrollIndicator={false}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        <View
          bg={Colors.white}
          paddingLeft={3}
          paddingRight={3}
          paddingBottom={3}
        >
          <Select
            minWidth="200"
            accessibilityLabel="Chọn danh mục"
            placeholder="Chọn danh mục"
            _selectedItem={{
              bg: "teal.600",
              endIcon: <CheckIcon size="5" />,
            }}
            mt={1}
            onValueChange={(itemValue) => {
              if (itemValue === "Tất cả") {
                setCategory("");
              } else {
                setCategory(itemValue);
              }
            }}
          >
            <Select.Item label="Tất cả" value="Tất cả" />
            {categories.map((data) => (
              <Select.Item label={data} value={data} />
            ))}
          </Select>

          <Select
            accessibilityLabel="Chọn thành phố"
            placeholder="Chọn thành phố"
            _selectedItem={{
              bg: "teal.600",
              endIcon: <CheckIcon size="5" />,
            }}
            mt={1}
            onValueChange={(itemValue) => {
              if (itemValue === "Tất cả") {
                setCity("");
              } else {
                setCity(itemValue);
              }
            }}
          >
            <Select.Item label="Tất cả" value="Tất cả" />
            {cities.map((data) => (
              <Select.Item label={data} value={data} />
            ))}
          </Select>
          <View mt={3}>
            <Button
              bg={"#FFA54F"}
              _pressed={{ bg: "#FFA54F" }}
              onPress={() => {
                setLoading(true);
                fetchProducts();
                wait(500).then(() => {
                  setLoading(false);
                });
              }}
            >
              <Text color={"white"}>Áp dụng</Text>
            </Button>
          </View>
        </View>
        <SlideScreen />
        {loading ? (
          <View flex={1} justifyContent="center" alignItems="center">
            <ActivityIndicator size="large" />
          </View>
        ) : pro.length > 0 ? (
          <Box bg={Colors.white} mt={2} pb={5}>
            <HomeProducts products={pro} />
          </Box>
        ) : (
          <View>
            <Text>Ko có</Text>
          </View>
        )}
      </ScrollView>
    </Box>
  );
}

export default HomeScreen;
