import {
    Box,
    Text,
    ScrollView,
    Image,
    Heading,
    HStack,
    View,
    Input,
    Pressable,
    VStack,
  } from "native-base";
  import React, { useState } from "react";
  import Colors from "../color";
  import Rating from "../Components/Rating";
  import { AntDesign, EvilIcons, Ionicons } from "@expo/vector-icons";
  
  function SimiliarProductScreen({navigation}) {
    const [value, setValue] = useState(0);
    return (
      <Box safeAreaTop flex={1} bg={Colors.white}>
        <HStack
          space={3}
          w="full"
          px={4}
          bg="#FFA500"
          py={3}
          alignItems="center"
          safeAreaTop
        >
          <Pressable onPress={() => navigation.goBack()}>
            <Ionicons name="arrow-back" size={24} color="white" />
          </Pressable>
          <View
            flex={1}
            flexDirection="row"
            alignItems="center"
            bg={Colors.white}
            rounded={5}
          >
            
          </View>
  
          <Pressable ml={3} onPress={() => navigation.navigate("Cart")}>
            <AntDesign name="shoppingcart" size={26} color="white" />
            <Box
              px={1}
              rounded="full"
              position="absolute"
              top={-5}
              left={3}
              bg={Colors.red}
              _text={{ color: Colors.white, fontSize: "11px" }}
            >
              5
            </Box>
          </Pressable>
        </HStack>
        <ScrollView px={3} showsVerticalScrollIndicator={false}>
          
        </ScrollView>
      </Box>
    );
  }
  
  export default SimiliarProductScreen;
