import React from "react";
import { Center } from "native-base";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import Colors from "../color";
import { StyleSheet } from "react-native";
import { FontAwesome, Ionicons, SimpleLineIcons } from "@expo/vector-icons";
import CartScreen from "../Screens/CartScreen";
import StackNav from "./StackNav";
import ProfileScreen2 from "../Screens/ProfileScreen2";
import { StackActions, NavigationActions } from "@react-navigation/native";

const Tab = createBottomTabNavigator();

function BottomNav() {
  return (
    <Tab.Navigator
      backBehavior="Trang chủ"
      initialRouteName="Trang chủ"
      screenOptions={{
        tabBarStyle: { ...styles.tab },
        headerShown: false,
        tabBarHideOnKeyboard: true,
      }}
    >
      <Tab.Screen
        name="Trang chủ"
        component={StackNav}
        listeners={({ navigation }) => ({
          tabPress: (e) => {
            navigation.reset({
              index: 0,
              routes: [{ name: "Profile" }],
            });
          },
        })}
        options={{
          tabBarActiveTintColor: "#FFA500",
          tabBarInactiveTintColor: "gray",
          tabBarStyle: { paddingBottom: 5 },
          tabBarIcon: ({ focused }) => (
            <Center>
              {focused ? (
                <Ionicons name="home-sharp" size={24} color="#FFA500" />
              ) : (
                <Ionicons name="home-outline" size={24} color="gray" />
              )}
            </Center>
          ),
        }}
      />
      {/* CART */}
      <Tab.Screen
        name="Đăng tin"
        component={CartScreen}
        options={{
          tabBarActiveTintColor: "#FFA500",
          tabBarInactiveTintColor: "gray",
          tabBarIcon: ({ focused }) => (
            <Center>
              {focused ? (
                <SimpleLineIcons name="note" size={24} color="#FFA500" />
              ) : (
                <SimpleLineIcons name="note" size={24} color="gray" />
              )}
            </Center>
          ),
        }}
      />

      {/* PROFILE */}

      <Tab.Screen
        name="Cá nhân"
        component={ProfileScreen2}
        options={{
          tabBarActiveTintColor: "#FFA500",
          tabBarInactiveTintColor: "gray",
          tabBarIcon: ({ focused }) => (
            <Center>
              {focused ? (
                <FontAwesome name="user" size={24} color="#FFA500" />
              ) : (
                <FontAwesome name="user-o" size={24} color="gray" />
              )}
            </Center>
          ),
        }}
      />
    </Tab.Navigator>
  );
}

const styles = StyleSheet.create({
  tab: {
    elevation: 0,
    backgroundColor: Colors.white,
    height: 50,
  },
});

export default BottomNav;
