import React from "react";
import {
  Text,
  Box,
  ScrollView,
  Image,
  Button,
  Center,
  HStack,
  VStack,
} from "native-base";
import HomeSearch from "../Components/HomeSearch";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";

const categories = [
  {
    label: "Xe cộ",
    image: require("../../assets/xeco.png"),
  },
  {
    label: "Giải trí, thể thao",
    image: require("../../assets/giaitri.png"),
  },
  {
    label: "Đồ gia dụng, nội thất, cây cảnh",
    image: require("../../assets/noithat.jpg"),
  },
  {
    label: "Thời trang",
    image: require("../../assets/thoitrang.jpg"),
  },
  {
    label: "Tủ lạnh, máy lạnh",
    image: require("../../assets/tulanh.jpg"),
  },
  {
    label: "Đồ điện tử",
    image:
    require("../../assets/dodientu.jpg"),
  },
];

function HomeScreen() {
  return (
    <Box flex={1} bg={Colors.gray}>
      <HomeSearch />
      <ScrollView showsVerticalScrollIndicator={false}>
        <Box bg={Colors.white} height="100%" mt={2}>
          <Text bold p={3} fontSize={15}>
            Khám phá danh mục
          </Text>
          <ScrollView showsHorizontalScrollIndicator={false} horizontal={false}>
            <HStack>
            {categories.slice(1,3).map((i, index) => (
              <Button
                key={index}
                variant="ghost"
                alignItems="center"
                p={0}
                pt={2}
                _pressed={{bg: "#E8E8E8"}}
              >
                <Center>
                  <Image
                    source={i.image}
                    alt="Xe"
                    h={70}
                    flex={1}
                    w={70}
                    resizeMode="contain"
                    rounded={10}
                  />
                </Center>
                <Text textAlign="center" flex={1} width={130} >
                  {i.label}
                </Text>
              </Button>
            ))}
            </HStack>
            <HStack>
            {categories[3,4,5].map((i, index) => (
              <Button
                key={index}
                variant="ghost"
                alignItems="center"
                p={0}
                pt={2}
                _pressed={{bg: "#E8E8E8"}}
              >
                <Center>
                  <Image
                    source={i.image}
                    alt="Xe"
                    h={70}
                    flex={1}
                    w={70}
                    resizeMode="contain"
                    rounded={10}
                  />
                </Center>
                <Text textAlign="center" flex={1} width={130} >
                  {i.label}
                </Text>
              </Button>
            ))}
            </HStack>
            
          </ScrollView>
        </Box>
      </ScrollView>
    </Box>
  );
}

export default HomeScreen;
