import { useNavigation } from "@react-navigation/native";
import { Flex, ScrollView, Text, Image, Box, HStack } from "native-base";
import React from "react";
import { FontAwesome } from "@expo/vector-icons";
import { TouchableOpacity } from "react-native";
import Colors from "../color";
import Rating from "./Rating";

function HomeProducts({ products }) {
  const navigation = useNavigation();
  return (
    <ScrollView flex={1} showsVerticalScrollIndicator={false}>
      <Flex flexWrap="wrap" direction="row" px={2}>
        {products.map((product) => (
          <TouchableOpacity
            key={product._id}
            style={{
              width: "50%",
              backgroundColor: Colors.white,
              borderStyle: "solid",
              borderWidth: 0.2,
              borderColor: "#E8E8E8",
              paddingBottom: 15,
              overflow: "hidden",
            }}
            onPress={() => navigation.navigate("Single", product)}
          >
            <Image
              source={{ uri: product.image }}
              alt={product.name}
              w="full"
              h={24}
              resizeMode="contain"
            />
            <Box px={4} pt={1}>
              <Text fontSize={15} isTruncated>
                {product.name}
              </Text>
              <HStack justifyContent="space-between">
                <Text fontSize={15} color={Colors.red} bold mt={1}>
                  {product.price}đ
                </Text>
                <Rating value={product.rating} />
              </HStack>
              <HStack alignItems="center" justifyContent="space-evenly">
                <FontAwesome name="user-circle-o" size={15} color="#A9A9A9" />
                <Text fontSize={10} color={Colors.deepestGray} mt={1}>
                  24/11/2022
                </Text>
                <Text fontSize={10} color={Colors.deepestGray} mt={1}>
                  tại Hà nội
                </Text>
              </HStack>
            </Box>
          </TouchableOpacity>
        ))}
      </Flex>
    </ScrollView>
  );
}

export default HomeProducts;
