import { Box, Text, ScrollView, View, Image, VStack, Input } from "native-base";
import React, { useEffect, useState } from "react";
import Colors from "../color";
import { Pressable, TouchableOpacity } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../Redux/Actions/UserActions";
import axios from "axios";

function ChangePasswordScreen({ navigation }) {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  // useEffect(() => {
  //   setTimeout(() => {
  //     fetchProfile();
  //     console.log(userInfo);
  //   }, 1000);
  // }, []);

  const dispatch = useDispatch();
  const logoutHander = async () => {
    dispatch(logout());
  };

  return (
    <Box safeAreaTop bg={Colors.gray}>
      <View space={3} w="full" px={4} bg="#FFA500" py={3} alignItems="center">
        <Text bold fontSize={17} color="white">
          Thay đổi mật khẩu
        </Text>
      </View>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View bg={Colors.white} mt={5}>
          <VStack>
            <Input></Input>
          </VStack>
        </View>
      </ScrollView>
    </Box>
  );
}

export default ChangePasswordScreen;
