import React, { useEffect, useState, useRef } from "react";
import { Image, View, ScrollView, Text } from "native-base";
import { InteractionManager, StyleSheet } from "react-native";
const imageSlide = [
  "https://img.freepik.com/premium-vector/modern-sale-banner-website-slider-template-design_54925-45.jpg?w=2000",
  "https://sastastoreonline.com/wp-content/uploads/2020/06/slider-3-1.jpg",
  "https://www.bharattaxi.com/blog/wp-content/uploads/2020/04/modern-sale-banner-website-slider-template-design_54925-44.jpg",
  "https://www.gannett-cdn.com/presto/2021/10/06/USAT/b051b85b-ccd4-4439-a4f1-61f92887fef6-2021_10_06_DEALS_BlackFridayFAQ_Hero.png",
];

function SlideScreen() {
  const [imgActive, setImageActive] = useState(0);

  const onchange = (nativeEvent) => {
    if (nativeEvent) {
      const slide = Math.ceil(
        nativeEvent.contentOffset.x / nativeEvent.layoutMeasurement.width
      );
      if (slide != imgActive) {
        setImageActive(slide);
      }
    }
  };
  const scrollRef = useRef();
  scrollRef.current?.scrollTo({
    x: scrollRef.current.x-1,
    animated: true
  });
  return (
    <View mt={2} bg="white">
      <ScrollView
        showsHorizontalScrollIndicator={false}
        horizontal
        pagingEnabled
        ref={scrollRef}
        onScroll={({ nativeEvent }) => onchange(nativeEvent)}
      >
        {imageSlide.map((e, index) => (
          <Image
            key={index}
            resizeMode="stretch"
            alt="slide"
            source={{ uri: e }}
            w={400}
            h={150}
          />
        ))}
      </ScrollView>
      <View
        position="absolute"
        flexDirection="row"
        alignSelf="center"
        bottom={0}
      >
        {imageSlide.map((e, index) => (
          <Text
            key={e}
            fontSize={30}
            style={
              imgActive == index
                ? { color: "black", margin: 3 }
                : { color: "white", margin: 3 }
            }
          >
            â€¢
          </Text>
        ))}
      </View>
    </View>
  );
}

export default SlideScreen;
