import { Box, Text, ScrollView, HStack, Pressable, View, Image, VStack, Input } from "native-base";
import React from "react";
import Colors from "../color";
import { Ionicons } from "@expo/vector-icons";
import { TouchableOpacity } from "react-native";

function UpdateProfileScreen({ navigation }) {
  return (
    <Box safeAreaTop bg={Colors.white}>
      {/* Header */}
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Cài đặt thông tin
        </Text>
      </HStack>
      {/* Body */}
      <ScrollView showsVerticalScrollIndicator={false} py={3}>
        <View
          flexDirection="row"
          bg={Colors.white}
          w="full"
          px={3}
          alignItems="center"
        >
          <Image
            source={require("../../assets/avatar.jpg")}
            h={20}
            w={20}
            alt="avatar"
            rounded="full"
          />
          <VStack ml={5} borderWidth={0.5} flex={1} p={3} rounded={5} borderColor="gray.300">
            <Text fontSize={13} color="gray.500">
              Họ và tên
            </Text>
            <Input
              borderWidth={0}
              p={0}
              _focus={{bg:Colors.white}}
            />
          </VStack>
        </View>
      </ScrollView>
    </Box>
  );
}

export default UpdateProfileScreen;
