import React from "react";
import { View, Text, Box, Center, ScrollView, Image } from "native-base";
import { TouchableOpacity } from "react-native";
import Colors from "../color";

function SimiliarProducts() {
  return (
    <View pb={10}>
      <View
        flexDirection="row"
        justifyContent="space-between"
        borderBottomColor="#B5B5B5"
        borderBottomWidth={0.2}
        pb={2}
      >
        <Text bold>Tin đăng tương tự</Text>
        <TouchableOpacity>
          <Text bold color="#436EEE">
            Xem tất cả {">"}
          </Text>
        </TouchableOpacity>
      </View>
      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
      >
        <TouchableOpacity>
            <Image  />
            <Text>Nước hoa nam Luxury Joseph</Text>
            <Text>1.250.000 đ</Text>
        </TouchableOpacity>
      </ScrollView>
    </View>
  );
}

export default SimiliarProducts;
