import React from "react";
import { ScrollView, Image } from "native-base";

const imageSlide = [
  "https://img.freepik.com/premium-vector/modern-sale-banner-website-slider-template-design_54925-45.jpg?w=2000",
  "https://sastastoreonline.com/wp-content/uploads/2020/06/slider-3-1.jpg",
];

function SlideScreen() {
  return (
    <ScrollView //onScroll={({nativeEvent}) => onchange(nativeEvent)}
      showsHorizontalScrollIndicator={false}
      pagingEnabled
      horizontal
      flex={1}
    >
      {imageSlide.map((e, index) => (
        <Image
          key={index}
          resizeMode="stretch"
          alt="slide"
          source={{ uri: e }}
          height={100}
          width={1000}
        />
      ))}
    </ScrollView>
  );
}

export default SlideScreen;
