import React from "react";
import {
  Box,
  Center,
  HStack,
  Pressable,
  ScrollView,
  Text,
  View,
  VStack,
} from "native-base";
import Colors from "../../color";
import { FontAwesome } from "@expo/vector-icons";

function Other() {
  return (
    <Box h="full" bg={Colors.gray}>
      <Pressable bg={Colors.white} p={2} alignItems="center" shadow={1} >
        <HStack>
          <Text fontSize={16}>L·ªçc tin</Text>
          <FontAwesome name="sort-down" size={16} color="black" style={{marginLeft: 5}}/>
        </HStack>
      </Pressable>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View mt={2} bg={Colors.white}>
          <Text>a</Text>
        </View>
      </ScrollView>
    </Box>
  );
}

export default Other;
