import { createSlice } from "@reduxjs/toolkit";
import { Storage } from "expo-storage";

const initialState = {
  userLogin: {},
};
const URL = `http://192.168.1.6:5000`;

const userInStorage = async (state) => {
  const user = await Storage.getItem({ key: "userInfo" });
  if (user != null) {
    state.userLogin = {
      ...state.userLogin,
      userLogin: JSON.parse(user),
    };
  } else {
    state.userLogin = { ...state.userLogin };
  }
};

export const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    login: async (state, action) => {
      const email = action.payload.email;
      const password = action.payload.password;
      try {
        const config = {
          headers: {
            "Content-Type": "application/json",
          },
        };

        const { data } = await axios.post(
          `${URL}/api/users/login`,
          {
            email,
            password,
          },
          config
        );
        await Storage.setItem({ key: "userInfo", value: JSON.stringify(data) });
        console.log(data);
      } catch (error) {
        console.log(error);
        // dispatch({
        //   type: USER_LOGIN_FAIL,
        //   payload:
        //     error.response && error.response.data.message
        //       ? error.response.data.message
        //       : error.message,
        // });
      }
    },
  },
});

// Action creators are generated for each case reducer function
export const { login } = userSlice.actions;
export const selectUser = (state) => state.userLogin;
export default userSlice.reducer;
