import React from "react";
import { View, Text, Box, Center, ScrollView, Image } from "native-base";
import { TouchableOpacity } from "react-native";
import Colors from "../color";
import products from "../data/Products";
import Rating from "../Components/Rating";
import { useNavigation } from "@react-navigation/native";
function SimiliarProducts() {
  const navigation = useNavigation();
  return (
    <View pb={10}>
      <View
        flexDirection="row"
        justifyContent="space-between"
        borderBottomColor="#B5B5B5"
        borderBottomWidth={0.2}
        pb={2}
      >
        <Text bold>Tin đăng tương tự</Text>
        <TouchableOpacity onPress={()=>navigation.navigate("SimiliarProduct")}>
          <Text bold color="#436EEE">
            Xem tất cả {">"}
          </Text>
        </TouchableOpacity>
      </View>
      <ScrollView horizontal showsHorizontalScrollIndicator={false}>
        {products.map((i, index) => (
          <TouchableOpacity alignItems="center" >
            <View mr={5} mt={5}>
              <Center>
                <Image
                  source={{
                    uri: i.image,
                  }}
                  alt="product"
                  h={120}
                  flex={1}
                  w="full"
                  resizeMode="cover"
                  rounded={3}
                />
              </Center>
              <Text flex={1} numberOfLines={2} isTruncated w={110} fontSize={16}>
                {i.name}
              </Text>
              <Text color={Colors.red} bold>
                {i.price} <Text underline>đ</Text>
              </Text>
              <Rating value={3}  />
              <Text color={Colors.deepestGray}>
                tại Đồng Nai
              </Text>
            </View>
          </TouchableOpacity>
        ))}
      </ScrollView>
    </View>
  );
}

export default SimiliarProducts;
