import {
  Box,
  Text,
  ScrollView,
  View,
  Image,
  VStack,
  HStack,
} from "native-base";
import React from "react";
import Colors from "../color";
import { Pressable, TouchableOpacity } from "react-native";
import { Ionicons, AntDesign } from "@expo/vector-icons";
function ProfileScreen({ navigation, route }) {
  const userInfo = route.params;
  return (
    <Box safeAreaTop bg={Colors.gray}>
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="black" />
        </Pressable>
        <Text bold fontSize={17} color="black">
          {userInfo.name}
        </Text>
      </HStack>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View
          flexDirection="row"
          bg={Colors.white}
          w="full"
          px={3}
          py={3}
          alignItems="center"
        >
          <Image
            source={require("../../assets/avatar.jpg")}
            h={20}
            w={20}
            alt="avatar"
            rounded="full"
          />
          {userInfo != null ? (
            <VStack ml={5}>
              <Text bold fontSize={17} ml={5}>
                {userInfo.name}
              </Text>
              <TouchableOpacity
                style={{
                  borderWidth: 1,
                  borderRadius: 25,
                  alignItems: "center",
                  textAlign: "center",
                  marginTop: 2,
                  color: Colors.white,
                  borderColor: Colors.deepestGray,
                  padding: 5,
                  paddingRight: 10,
                  paddingLeft: 10,
                }}
                onPress={() => {
                  navigation.navigate("UpdateProfile", userInfo);
                }}
              >
                <Text fontSize={12}>Chỉnh sửa thông tin</Text>
              </TouchableOpacity>
            </VStack>
          ) : (
            <Pressable
              bold
              fontSize={17}
              onPress={() => {
                navigation.navigate("Login");
              }}
            >
              <Text bold fontSize={16} ml={5}>
                Đăng nhập/Đăng ký
              </Text>
            </Pressable>
          )}
        </View>
        <View bg={Colors.white}>
          <VStack>
            <TouchableOpacity
              style={{
                flex: 1,
                flexDirection: "row",
                alignItems: "center",
                padding: 10,
                borderTopWidth: 0.6,
                borderTopColor: Colors.deepGray,
              }}
            >
              <AntDesign name="staro" size={14} color="black" />
              <Text fontSize={14} ml={2}>
                Đơn mua
              </Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={{
                flex: 1,
                flexDirection: "row",
                alignItems: "center",
                padding: 10,
              }}
            >
              <Image
                source={require("../../assets/icon2.png")}
                alt="icon"
                h={7}
                w={7}
              />
              <Text fontSize={15} ml={2}>
                Đơn bán
              </Text>
            </TouchableOpacity>
          </VStack>
        </View>
        <Text color="#808080" bold fontSize={15} p={3}>
          Tiện ích
        </Text>
        <View bg={Colors.white} mb={20}>
          <VStack>
            <TouchableOpacity
              style={{
                flex: 1,
                flexDirection: "row",
                alignItems: "center",
                padding: 10,
                borderBottomWidth: 0.6,
                borderBottomColor: Colors.deepGray,
              }}
            >
              <Image
                source={require("../../assets/icon3.png")}
                alt="icon"
                h={7}
                w={7}
              />
              <Text fontSize={15} ml={2}>
                Tin đăng đã lưu
              </Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={{
                flex: 1,
                flexDirection: "row",
                alignItems: "center",
                padding: 10,
              }}
              onPress={() => {
                userInfo != null
                  ? navigation.navigate("UpdateProfile", userInfo)
                  : navigation.navigate("Login");
              }}
            >
              <Image
                source={require("../../assets/icon4.png")}
                alt="icon"
                h={7}
                w={7}
              />
              <Text fontSize={15} ml={2}>
                Cài đặt tài khoản
              </Text>
            </TouchableOpacity>
            {userInfo != null ? (
              <TouchableOpacity
                style={{
                  flex: 1,
                  flexDirection: "row",
                  alignItems: "center",
                  padding: 10,
                }}
                onPress={() => {
                  logoutHander();
                }}
              >
                <Image
                  source={require("../../assets/icon5.png")}
                  alt="icon"
                  h={7}
                  w={7}
                />
                <Text fontSize={15} ml={2}>
                  Đăng xuất
                </Text>
              </TouchableOpacity>
            ) : (
              ""
            )}
          </VStack>
        </View>
      </ScrollView>
    </Box>
  );
}

export default ProfileScreen;
