import {
  Box,
  Text,
  ScrollView,
  View,
  Image,
  VStack,
  HStack,
} from "native-base";
import React from "react";
import Colors from "../color";
import { Pressable, TouchableOpacity } from "react-native";
import { Ionicons, AntDesign, Fontisto } from "@expo/vector-icons";
function ProfileScreen({ navigation, route }) {
  const userInfo = route.params;
  return (
    <Box safeAreaTop bg={Colors.gray}>
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="black" />
        </Pressable>
        <Text bold fontSize={17} color="black">
          {userInfo.name}
        </Text>
      </HStack>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View
          flexDirection="row"
          bg={Colors.white}
          w="full"
          px={3}
          py={3}
          alignItems="center"
        >
          <Image
            source={require("../../assets/avatar.jpg")}
            h={20}
            w={20}
            alt="avatar"
            rounded="full"
          />
          <VStack ml={5}>
            <Text bold fontSize={17} ml={5}>
              {userInfo.name}
            </Text>
            <TouchableOpacity
              style={{
                borderWidth: 1,
                borderRadius: 25,
                alignItems: "center",
                textAlign: "center",
                marginTop: 2,
                color: Colors.white,
                borderColor: Colors.deepestGray,
                padding: 5,
                paddingRight: 10,
                paddingLeft: 10,
              }}
              onPress={() => {
                navigation.navigate("UpdateProfile", userInfo);
              }}
            >
              <Text fontSize={12}>Chỉnh sửa thông tin</Text>
            </TouchableOpacity>
          </VStack>
        </View>
        <View bg={Colors.white}>
          <VStack>
            <View
              style={{
                flex: 1,
                flexDirection: "row",
                alignItems: "center",
                padding: 10,
                borderTopWidth: 0.6,
                borderTopColor: Colors.deepGray,
              }}
            >
              <AntDesign name="staro" size={14} color="black" />
              <Text fontSize={14} ml={2} color="gray.400">
                Đánh giá: Chưa có đánh giá
              </Text>
            </View>
            <View
              style={{
                flex: 1,
                flexDirection: "row",
                alignItems: "center",
                padding: 10,
              }}
            >
              <Fontisto name="date" size={14} color="black" />
              <Text fontSize={14} ml={2} color="gray.400">
                Ngày tham gia:
              </Text>
              <Text fontSize={14}> {userInfo.createdAt}</Text>
            </View>
          </VStack>
        </View>
        <Text color="#808080" bold fontSize={15} p={3}>
          Tin đang đăng
        </Text>
        <View bg={Colors.white} mb={20}>
          <VStack>
            <TouchableOpacity
              style={{
                flex: 1,
                flexDirection: "row",
                alignItems: "center",
                padding: 10,
                borderBottomWidth: 0.6,
                borderBottomColor: Colors.deepGray,
              }}
            >
              <Image
                source={{
                  uri: "https://is4.revolveassets.com/images/p4/n/z/JOHF-MK23_V1.jpg",
                }}
                alt="icon"
                h={20}
                w="25%"
                resizeMode="contain"
              />
              <VStack>
                <Text fontSize={15} ml={2} isTruncated w={250}>
                  Áo hoodie đen 90% new
                </Text>
                <Text fontSize={15} ml={2} bold color="red.500">
                  100000đ
                </Text>
              </VStack>
            </TouchableOpacity>
          </VStack>
        </View>
      </ScrollView>
    </Box>
  );
}

export default ProfileScreen;
