import {
  Box,
  Text,
  ScrollView,
  View,
  VStack,
  Input,
  HStack,
  FormControl,
  WarningOutlineIcon,
} from "native-base";
import React, { useEffect, useState } from "react";
import Colors from "../color";
import { Pressable } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../Redux/Actions/UserActions";
import axios from "axios";
import CustomButton from "../Components/CustomButton";
import { Ionicons } from "@expo/vector-icons";
import { Storage } from "expo-storage";
function ChangePasswordScreen({ navigation }) {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const [oldpass, setOldPass] = useState(null);
  const [pw, setPw] = useState(null);
  const pass = async () => {
    const getPass = await Storage.getItem({ key: "pass" });
    setPw(getPass);
  };
  useEffect(() => {
    pass();
  }, []);
  // useEffect(() => {
  //   setTimeout(() => {
  //     fetchProfile();
  //     console.log(userInfo);
  //   }, 1000);
  // }, []);

  const dispatch = useDispatch();
  const logoutHander = async () => {
    dispatch(logout());
  };

  return (
    <Box safeAreaTop bg={Colors.gray}>
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Thay đổi mật khẩu
        </Text>
      </HStack>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View bg={Colors.white} mt={5}>
          <VStack p={5}>
            <FormControl isRequired>
              <Input
                passwordRules={{ minlength: 8 }}
                fontSize={15}
                placeholder="Mật khẩu cũ"
                mt={2}
                type="password"
                onChangeText={(e) => setOldPass(e)}
              />
              <FormControl.ErrorMessage
                leftIcon={<WarningOutlineIcon size="xs" />}
              >
                Something is wrong.
              </FormControl.ErrorMessage>
              <Input
                type="password"
                fontSize={15}
                placeholder="Mật khẩu mới"
                mt={2}
              ></Input>
              <FormControl.HelperText>
                Must be atleast 6 characters.
              </FormControl.HelperText>
              <Input
                fontSize={15}
                type="password"
                placeholder="Nhập lại mật khẩu"
                mt={2}
                mb={5}
              ></Input>
            </FormControl>

            <CustomButton label={"CẬP NHẬP"} bg="#F4A460" disabled={true} />
          </VStack>
        </View>
      </ScrollView>
    </Box>
  );
}

export default ChangePasswordScreen;
