import React from 'react';
import { Box, FormControl, Input, ScrollView, VStack } from 'native-base';
import Colors from '../../color';
import Buttone from '../Buttone';

const Inputs = [
    {
        Label: "USERNAME",
        type: "text",
    },
    {
        Label: "EMAIL",
        type: "email",
    },
    {
        Label: "NEW PASSWORD",
        type: "password",
    },
    {
        Label: "CONFIRM PASSWORD",
        type: "password",
    }
];

function Profile() {
  return (
    <Box h="full" bg={Colors.white} px={5}>
        <ScrollView showsVerticalScrollIndicator={false}>
            <VStack space={10} mt={5} pb={10}>
                {
                    Inputs.map((i, index) => (
                        <FormControl key={index}>
                            <FormControl.Label _text={{
                                fontSize: "12px",
                                fontWeight: "bold",
                            }}>
                                {i.Label}
                            </FormControl.Label>
                            <Input borderColor={Colors.main} borderWidth={0.2} bg={Colors.subGreen} py={4} color={Colors.main} fontSize={15} 
                                _focus={{bg: Colors.subGreen,borderWidth: 1,borderColor: Colors.main}}
                                type={i.type}
                            />
                        </FormControl>
                    ))
                }
                <Buttone bg={Colors.main} color={Colors.white}>UPDATE PROFILE</Buttone>
            </VStack>
        </ScrollView>
    </Box>
  );
}

export default Profile;
