import React, { useEffect, useState } from "react";
import {
  Text,
  Box,
  ScrollView,
  HStack,
  View,
  Input,
  Pressable,
  Select,
  CheckIcon,
} from "native-base";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";
import { ActivityIndicator, RefreshControl } from "react-native";
import { Ionicons, EvilIcons } from "@expo/vector-icons";
import axios from "axios";

const categories = [
  {
    label: "Xe cộ",
    image: require("../../assets/xeco.png"),
  },
  {
    label: "Giải trí, thể thao",
    image: require("../../assets/giaitri.png"),
  },
  {
    label: "Đồ gia dụng, nội thất, cây cảnh",
    image: require("../../assets/noithat.jpg"),
  },
  {
    label: "Thời trang",
    image: require("../../assets/thoitrang.jpg"),
  },
  {
    label: "Tủ lạnh, máy lạnh",
    image: require("../../assets/tulanh.jpg"),
  },
  {
    label: "Đồ điện tử",
    image: require("../../assets/dodientu.jpg"),
  },
];
const Url = `http://192.168.1.3:5000`;

function SearchScreen({ navigation, route }) {
  const [pro, setPro] = useState([]);
  const [category, setCategory] = useState("");
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const wait = (timeout) => {
    return new Promise((resolve) => setTimeout(resolve, timeout));
  };
  const data = route.params;
  console.log(data);
  const fetchProducts = async () => {
    try {
      const { data } = await axios.get(`${Url}/api/product/show/accept`);
      if (search.trim() != "" || category.trim() != null) {
        setPro(
          data.filter(
            (data) =>
              data.title
                .toString()
                .toLowerCase()
                .includes(search.toString().toLowerCase()) &&
              data.category.includes(category)
          )
        );
      } else {
        setPro(data);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const searchCate = (itemValue) => {
    if (itemValue === "Tất cả") {
      setCategory("");
    } else {
      setCategory(itemValue);
      wait(1000).then(() => {
        fetchProducts();
      });
    }
  };

  useEffect(() => {
    fetchProducts();
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  }, []);

  const [refreshing, setRefreshing] = React.useState(false);
  const onRefresh = React.useCallback(() => {
    setRefreshing(true);
    setLoading(true);
    fetchProducts();
    wait(1000).then(() => {
      setRefreshing(false);
      setLoading(false);
    });
  }, []);
  return (
    <Box bg={Colors.gray} flex={1}>
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <View
          flex={1}
          flexDirection="row"
          alignItems="center"
          bg={Colors.white}
          rounded={5}
        >
          <EvilIcons
            name="search"
            size={24}
            color="#8B8B7A"
            style={{ marginLeft: 5 }}
          />
          <Input
            placeholder="Tìm kiếm "
            w="85%"
            size={10}
            pl={2}
            bg={Colors.white}
            flex={1}
            type="search"
            h={9}
            borderWidth={0}
            _focus={{ bg: Colors.white }}
            onChangeText={(e) => {
              e = e.replace(/  +/g, " ");
              setSearch(e.replace(/^[ \t]+|[ \t]+$/gm, ""));
            }}
            onEndEditing={() => {
              fetchProducts();
            }}
            variant="filled"
          />
        </View>
      </HStack>
      {loading ? (
        <View flex={1} justifyContent="center" alignItems="center">
          <ActivityIndicator size="large" />
        </View>
      ) : (
        <ScrollView
          showsVerticalScrollIndicator={false}
          refreshControl={
            <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
          }
        >
          <View bg={Colors.white} p={2}>
            <Select
              minWidth="200"
              accessibilityLabel="Chọn danh mục"
              placeholder="Chọn danh mục"
              _selectedItem={{
                bg: "teal.600",
                endIcon: <CheckIcon size="5" />,
              }}
              mt={1}
              onValueChange={(itemValue) => {
                if (itemValue === "Tất cả") {
                  setCategory("");
                } else {
                  setCategory(itemValue);
                  wait(1000).then(() => {
                    fetchProducts();
                  });
                }
              }}
            >
              <Select.Item label="Tất cả" value="Tất cả" />
              <Select.Item
                label="Thời trang, đồ dùng cá nhân"
                value="Thời trang, đồ dùng cá nhân"
              />
              <Select.Item label="Đồ điện tử" value="Đồ điện tử" />
              <Select.Item
                label="Giải trí thể thao"
                value="Giải trí thể thao"
              />
            </Select>
          </View>
          {pro.length > 0 ? (
            <Box bg={Colors.white} mt={2} pb={5}>
              <HomeProducts products={pro} />
            </Box>
          ) : (
            <View>
              <Text>Ko có</Text>
            </View>
          )}
        </ScrollView>
      )}
    </Box>
  );
}

export default SearchScreen;
