import { Flex, ScrollView, Text, Image } from 'native-base';
import React from 'react';
import { Pressable } from 'react-native';
import Colors from '../color';
import products from '../data/Products';

function HomeProducts() {
  return (
    <ScrollView flex={1}>
        <Flex flexWrap="wrap" direction="row" justifyContent="space-between" px={6}> 
            {products.map((product) => (
                <Pressable key={product._id} w="47%" bg={Colors.white} 
                    rounded="md" shadow={2} pt={0.3} my={3} pb={2} overflow="hiden">
                        <Image source={{uri:product.image}} alt={product.name} w="full" 
                         h={24} resizeM></Image>
                </Pressable>
            ))}
        </Flex>
    </ScrollView>
  );
}

export default HomeProducts;
