import React from "react";
import {
  View,
  Text,
  Box,
  ScrollView,
  Image,
  Button,
} from "native-base";
import HomeSearch from "../Components/HomeSearch";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";
import { TouchableOpacity } from "react-native";

const categories = [
  {
    label: "Xe cộ",
    image:
      "https://rentcarsamui.com/wp-content/uploads/2022/04/cropped-Car-Motorbike-Rental-on-Koh-Samui.jpg",
  },
  {
    label: "Đồ điện tử",
    image:
      "https://tietkiemdiennang.net/wp-content/uploads/2020/08/thiet-bi-dien-tu-la-gi-1-2.jpg",
  },
  {
    label: "Đồ gia dụng, nội thất, cây cảnh",
    image: "https://lighthouse.chotot.com/_next/image?url=https%3A%2F%2Fstatic.chotot.com%2Fstorage%2Fchapy-pro%2Fnewcats%2Fv8%2F14000.png&w=256&q=95",
  },
  {
    label: "Xe cộ",
    image: "https://www.motoart.gr/assets/images/pics/mix-1.jpg",
  },
  {
    label: "Xe cộ",
    image: "https://www.motoart.gr/assets/images/pics/mix-1.jpg",
  },
  {
    label: "Xe cộ",
    image: "https://www.motoart.gr/assets/images/pics/mix-1.jpg",
  },
];

function HomeScreen() {
  return (
    <Box flex={1} bg={Colors.gray}>
      <HomeSearch />
      <ScrollView showsVerticalScrollIndicator={false}>
        <Box bg={Colors.white} height="80%" mt={2}>
          <Text bold p={3} fontSize={15}>
            Khám phá danh mục
          </Text>
          <ScrollView showsHorizontalScrollIndicator={false} horizontal={true}>
            {categories.map((i, index) => (
              <Button
                key={index}
                variant="ghost"
                // style={{ alignItems: "center", marginLeft: 10, width: 100}}
              >
                  <Image
                    source={{
                      uri: i.image,
                    }}
                    alt="Xe"
                    h={70}
                    w={70}
                    resizeMode="contain"
                    rounded={10}
                  />

                  <Text width={100}>{i.label}</Text>
              </Button>
            ))}
          </ScrollView>
        </Box>
      </ScrollView>
    </Box>
  );
}

export default HomeScreen;
