import React, { useEffect, useState } from "react";
import {
  Text,
  Box,
  ScrollView,
  Image,
  Button,
  Center,
  HStack,
} from "native-base";
import HomeSearch from "../Components/HomeSearch";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";
import SlideScreen from "../Components/SlideScreen";
import axios from "axios";

const categories = [
  {
    label: "Xe cộ",
    image: require("../../assets/xeco.png"),
  },
  {
    label: "Giải trí, thể thao",
    image: require("../../assets/giaitri.png"),
  },
  {
    label: "Đồ gia dụng, nội thất, cây cảnh",
    image: require("../../assets/noithat.jpg"),
  },
  {
    label: "Thời trang",
    image: require("../../assets/thoitrang.jpg"),
  },
  {
    label: "Tủ lạnh, máy lạnh",
    image: require("../../assets/tulanh.jpg"),
  },
  {
    label: "Đồ điện tử",
    image: require("../../assets/dodientu.jpg"),
  },
];

function HomeScreen() {
  const [products, setProducts] = useState([]);
  useEffect(() => {
    axios
      .get("/user?ID=12345")
      .then(function (response) {
        // handle success
        console.log(response);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
      .finally(function () {
        // always executed
      });
    const fetchProducts = async () => {
      const { data } = await axios.get("/api/products");
      setProducts(data);
      console.log(data);
    };
    fetchProducts();
  }, []);
  return (
    <Box bg={Colors.gray} flex={1}>
      <HomeSearch />
      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Slide */}
        <SlideScreen />
        {/* Categories */}
        <Box bg={Colors.white} mt={2} pb={5}>
          <Text bold p={3} fontSize={15}>
            Khám phá danh mục
          </Text>
          <HStack>
            {categories.slice(0, 3).map((i, index) => (
              <Button
                key={index}
                variant="ghost"
                alignItems="center"
                p={0}
                pt={2}
                _pressed={{ bg: "#E8E8E8" }}
              >
                <Center>
                  <Image
                    source={i.image}
                    alt="Xe"
                    h={70}
                    flex={1}
                    w={70}
                    resizeMode="contain"
                    rounded={10}
                  />
                </Center>
                <Text textAlign="center" flex={1} width={130}>
                  {i.label}
                </Text>
              </Button>
            ))}
          </HStack>

          <HStack>
            {categories.slice(3, 6).map((i, index) => (
              <Button
                key={index}
                variant="ghost"
                alignItems="center"
                p={0}
                pt={2}
                _pressed={{ bg: "#E8E8E8" }}
              >
                <Center>
                  <Image
                    source={i.image}
                    alt="Xe"
                    h={70}
                    flex={1}
                    w={70}
                    resizeMode="contain"
                    rounded={10}
                  />
                </Center>
                <Text textAlign="center" flex={1} width={130}>
                  {i.label}
                </Text>
              </Button>
            ))}
          </HStack>
        </Box>
        {/* Products */}
        <Box bg={Colors.white} mt={2} pb={5}>
          <Text bold p={3} fontSize={15}>
            Tin đăng mới
          </Text>
          <HomeProducts />
        </Box>
      </ScrollView>
    </Box>
  );
}

export default HomeScreen;
