import React from 'react';
import { View, Text, Box, Center,ScrollView,Image } from 'native-base';
import Colors from '../color';
import {FontAwesome} from '@expo/vector-icons';

function SlideScreen() {
  return (
    <ScrollView //onScroll={({nativeEvent}) => onchange(nativeEvent)}
          showsHorizontalScrollIndicator={false}
          pagingEnabled
          horizontal
        >
          {imageSlide.map((e,index) =>   
            <Image key={index} resizeMode="stretch" alt="slide" source={{uri: e}} height={HEIGH/10} width="100%"/>
          )}
        </ScrollView>
  );
}

export default SlideScreen;
