import React from "react";
import { View, Text, Box, Center, ScrollView } from "native-base";
import { TouchableOpacity } from "react-native";
import Colors from "../color";

function SimiliarProducts() {
  return (
    <View pb={10}>
      <View flexDirection="row" justifyContent="space-between" borderBottomColor="#828282" borderBottomWidth={0.5}>
        <Text bold>Tin đăng tương tự</Text>
        <TouchableOpacity>
          <Text bold color="#0000FF">Xem tất cả {">"}</Text>
        </TouchableOpacity>
      </View>
      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
      ></ScrollView>
    </View>
  );
}

export default SimiliarProducts;
