import { Box, Button, Heading, Input, VStack } from 'native-base'
import React from 'react'
import { Text, Pressable } from 'react-native'
import Colors from '../color'
import { MaterialCommunityIcons, Ionicons } from '@expo/vector-icons';

function LoginScreen({navigation}) {
  return (
    <Box flex = {1} bg = {Colors.white}>
        <Box 
        w = "full"
        h = "full"
        position = "absolute"
        top = "0"
        px = "6"
        justifyContent="center">
          <Heading>LOGIN</Heading>
          <VStack space={5} pt="6">
            {/* Email */}
            <Input variant="underlined" placeholder="user@gmail.com" w="70%"
              color={Colors.main} borderBottomColor={Colors.underline} pl={2}
              InputLeftElement={<MaterialCommunityIcons name="email" size={20} color={Colors.main} />}/>
            {/* Pass */}
            <Input variant="underlined" placeholder="***********" w="70%" type="password"
              color={Colors.main} borderBottomColor={Colors.underline} pl={2}
              InputLeftElement={<Ionicons name="eye" size={20} color={Colors.main} />}/>
          </VStack>
          <Button 
            _pressed={{
              bg: Colors.main,
            }}
            my={30} w="40%" rounded={50} bg={Colors.main} 
            onPress={()=>navigation.navigate('Bottom')}>
              LOGIN
          </Button>
          <Pressable mt={4} onPress={()=>navigation.navigate('Sig')}>
            <Text color={Colors.deepestGray}>SIGN UP</Text>
          </Pressable>
        </Box>
    </Box>
  )
}

export default LoginScreen
