import React from "react";
import { View, Text, Box, ScrollView, Image, VStack, Pressable } from "native-base";
import HomeSearch from "../Components/HomeSearch";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";
import { Touchable } from "react-native";

const categories = [
  {
    label: "Xe cộ",
    image:
      "https://images.summitmedia-digital.com/topgear/images/articleImages/news/0_2014/11/03/instagram_post/instagram-main1.jpg",
  },
  {
    label: "Xe cộ",
    image:
      "https://images.summitmedia-digital.com/topgear/images/articleImages/news/0_2014/11/03/instagram_post/instagram-main1.jpg",
  },
  {
    label: "Xe cộ",
    image:
      "https://images.summitmedia-digital.com/topgear/images/articleImages/news/0_2014/11/03/instagram_post/instagram-main1.jpg",
  },
  {
    label: "Xe cộ",
    image:
      "https://images.summitmedia-digital.com/topgear/images/articleImages/news/0_2014/11/03/instagram_post/instagram-main1.jpg",
  },
];

function HomeScreen() {
  return (
    <Box flex={1} bg={Colors.gray}>
      <HomeSearch />
      <ScrollView showsVerticalScrollIndicator={false}>
        <Box bg={Colors.white} h={40} mt={2}>
          <Text bold p={3} fontSize={15}>
            Khám phá danh mục
          </Text>
          <ScrollView showsHorizontalScrollIndicator={false} horizontal={true} p={3}>
            <Touchable alignItems="center">
              <Image
                source={{
                  uri: "https://www.motoart.gr/assets/images/pics/mix-1.jpg",
                }}
                alt="Xe"
                h={50}
                w={50}
                resizeMode="contain"
                rounded={10}
              />
              <Text>Xe co</Text>
            </Touchable>
            
          </ScrollView>
        </Box>
      </ScrollView>
    </Box>
  );
}

export default HomeScreen;
