import { Box, Text, ScrollView, HStack, Pressable } from "native-base";
import React from "react";
import Colors from "../color";
import { Ionicons } from "@expo/vector-icons";

function UpdateProfileScreen({ navigation }) {
  return (
    <Box safeAreaTop flex={1} bg={Colors.white}>
        {/* Header */}
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Cài đặt thông tin
        </Text>
      </HStack>
        {/* Body */}
      <ScrollView showsVerticalScrollIndicator={false} py={3}>
      <View
          flexDirection="row"
          bg={Colors.white}
          w="full"
          px={3}
          py={3}
          alignItems="center"
        >
          <Image
            source={require("../../assets/avatar.jpg")}
            h={20}
            w={20}
            alt="avatar"
            rounded="full"
          />
          <VStack ml={5}>
            <Text bold fontSize={17}>
              Trần Lý Thắng
            </Text>
            <TouchableOpacity
              style={{
                borderWidth: 1,
                borderRadius: 25,
                alignItems: "center",
                textAlign: "center",
                marginTop: 2,
                color: Colors.white,
                borderColor: Colors.deepestGray,
                padding: 5,
                paddingRight: 10,
                paddingLeft: 10,
              }}
            >
              <Text fontSize={12}>Chỉnh sửa trang cá nhân</Text>
            </TouchableOpacity>
          </VStack>
        </View>
      </ScrollView>
    </Box>
  );
}

export default UpdateProfileScreen;
