import { Box , Text, ScrollView, Image, Heading, HStack, Spacer} from 'native-base';
import React, { useState } from 'react'
import Colors from '../color';
import Rating from '../Components/Rating';
import NumbericInput from 'react-native-numeric-input';
import Buttone from '../Components/Buttone';
import Review from '../Components/Review';
import { useNavigation } from '@react-navigation/native';

function SingleProductScreen({route}) {
  const [value, setValue] = useState(0);
  const navigation = useNavigation();
  const product = route.params;
  return (
    <Box safeAreaTop flex={1} bg={Colors.white}>
      <ScrollView px={5} showsVerticalScrollIndicator={false}>
        <Image source={{uri:product.image}} alt="image" 
            w="full" h={300} resizeMode="contain"/>
        <Heading bold fontSize={15} mb={2} lineHeight={22}>
          {product.name}
        </Heading>
        <Rating value={product.rating} text={``}/>
        <HStack space={2} alignItems="center" my={5}>
          <NumbericInput 
            value={value}
            totalHeight={30}
            totalWidth={140}
            iconSize={25}
            step={1}
            maxValue={15}
            minValue={0}
            borderColor={Colors.deepGray}
            rounded
            textColor={Colors.black}
            iconStyle={{color: Colors.white}}
            rightButtonBackgroundColor={Colors.main}
            leftButtonBackgroundColor={Colors.main}
          />
          <Spacer/>
          <Heading bold color={Colors.black} fontSize={19}>${product.price}</Heading>
        </HStack>
        <Text lineHeight={24} fontSize={12}>
           Build and Deploy Modern React native ExpoE-commerce Mobile App Course: Build and Deploy Modern React native ExpoE-commerce Mobile App Course: Build and Deploy Modern React native ExpoE-commerce Mobile App Course: Build and Deploy Modern React native ExpoE-commerce Mobile App Course: Build and Deploy Modern React native Expo

        </Text>
        <Buttone bg={Colors.main} color={Colors.white} mt={10}>
          ADD TO CART
        </Buttone>
        <Review/>
      </ScrollView>
    </Box>
  );
}

export default SingleProductScreen;
