import { Box , View, Text, ScrollView, Image, Heading} from 'native-base';
import React from 'react'
import Colors from '../color';

function SingleProductScreen() {
  return (
    <Box safeAreaTop flex={1} bg={Colors.white}>
      <ScrollView px={5} showsVerticalScrollIndicator={false}>
        <Image source={{uri:"https://thoitrangbigsize.vn/wp-content/uploads/2019/09/1-6.jpg"}} alt="image" 
            w="full" h={300} resizeMode="contain"/>
        <Heading bold fontSize={15} mb={2} lineHeight={22}>
          New Adias shirt
        </Heading>
      </ScrollView>
    </Box>
  );
}

export default SingleProductScreen;
