import React from "react";
import {
  Text,
  Box,
  ScrollView,
  Image,
  Button,
  Center,
  HStack,
  View,
} from "native-base";
import HomeSearch from "../Components/HomeSearch";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";
import { Dimensions } from "react-native";

const categories = [
  {
    label: "Xe cộ",
    image: require("../../assets/xeco.png"),
  },
  {
    label: "Giải trí, thể thao",
    image: require("../../assets/giaitri.png"),
  },
  {
    label: "Đồ gia dụng, nội thất, cây cảnh",
    image: require("../../assets/noithat.jpg"),
  },
  {
    label: "Thời trang",
    image: require("../../assets/thoitrang.jpg"),
  },
  {
    label: "Tủ lạnh, máy lạnh",
    image: require("../../assets/tulanh.jpg"),
  },
  {
    label: "Đồ điện tử",
    image: require("../../assets/dodientu.jpg"),
  },
];
const WIDTH = Dimensions.get('window').width;
const HEIGH = Dimensions.get('window').height;

const imageSlide = [
  'https://img.freepik.com/premium-vector/modern-sale-banner-website-slider-template-design_54925-45.jpg?w=2000',
  'https://sastastoreonline.com/wp-content/uploads/2020/06/slider-3-1.jpg',
];
function HomeScreen() {

  onchange = (nativeEvent) => {

  }

  return (
    <Box flex={1} bg={Colors.gray}>
      <HomeSearch />
      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Slide */}
        <ScrollView //onScroll={({nativeEvent}) => onchange(nativeEvent)}
          showsHorizontalScrollIndicator={false}
          pagingEnabled
          horizontal
        >
          {imageSlide.map((e,index) =>   
            <Image key={e} resizeMode="stretch" alt="slide" source={{uri: e}} height={50} width="full"/>
          )}
        </ScrollView>
        {/* Categories */}
        <Box bg={Colors.white} mt={2} pb={5}>
          <Text bold p={3} fontSize={15}>
            Khám phá danh mục
          </Text>
          <HStack>
            {categories.slice(0, 3).map((i, index) => (
              <Button
                key={index}
                variant="ghost"
                alignItems="center"
                p={0}
                pt={2}
                _pressed={{ bg: "#E8E8E8" }}
              >
                <Center>
                  <Image
                    source={i.image}
                    alt="Xe"
                    h={70}
                    flex={1}
                    w={70}
                    resizeMode="contain"
                    rounded={10}
                  />
                </Center>
                <Text textAlign="center" flex={1} width={130}>
                  {i.label}
                </Text>
              </Button>
            ))}
          </HStack>

          <HStack>
            {categories.slice(3, 6).map((i, index) => (
              <Button
                key={index}
                variant="ghost"
                alignItems="center"
                p={0}
                pt={2}
                _pressed={{ bg: "#E8E8E8" }}
              >
                <Center>
                  <Image
                    source={i.image}
                    alt="Xe"
                    h={70}
                    flex={1}
                    w={70}
                    resizeMode="contain"
                    rounded={10}
                  />
                </Center>
                <Text textAlign="center" flex={1} width={130}>
                  {i.label}
                </Text>
              </Button>
            ))}
          </HStack>
        </Box>
        {/* Products */}
        <Box bg={Colors.white} mt={2} pb={5}>
          <Text bold p={3} fontSize={15}>
            Tin đăng mới
          </Text>
          <HomeProducts/>
        </Box>
      </ScrollView>
    </Box>
  );
}

export default HomeScreen;
