import React, { useEffect, useState } from "react";
import {
  Box,
  HStack,
  Image,
  Pressable,
  ScrollView,
  Spacer,
  Text,
  View,
} from "native-base";
import Colors from "../../color";
import { FontAwesome } from "@expo/vector-icons";

function Other() {
  const [product, setProduct] = useState([]);
  const Url = `http://192.168.1.6:5000`;
  const fetchProducts = async () => {
    try {
      const id = await Storage.getItem({ key: "userID" });
      const { data } = await axios.get(`${Url}/api/product/users/${id}`);
      if (data.length > 0) {
        setProduct(
          data.filter((data) => data.isShow === false || data.isAccept === 0)
        );
      } else {
        setProduct([]);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const defaultOptions = {
    significantDigits: 2,
    thousandsSeparator: ",",
    decimalSeparator: ".",
    symbol: "",
  };

  const currencyFormatter = (value, options) => {
    if (typeof value !== "number") value = 0.0;
    options = { ...defaultOptions, ...options };
    value = value.toFixed(options.significantDigits);

    const [currency, decimal] = value.split(".");
    return `${currency.replace(
      /\B(?=(\d{3})+(?!\d))/g,
      options.thousandsSeparator
    )}`;
  };
  const [show, setShow] = useState(false);
  return (
    <Box h="full" bg={Colors.gray}>
      <Pressable
        bg={Colors.white}
        p={2}
        alignItems="center"
        shadow={1}
        onPress={() => (show ? setShow(false) : setShow(true))}
      >
        <HStack>
          <Text fontSize={16}>Lọc tin</Text>
          <FontAwesome
            name="sort-down"
            size={16}
            color="black"
            style={{ marginLeft: 5 }}
          />
        </HStack>
      </Pressable>
      {show && (
        <View bg={Colors.white} mt={1}>
          <Pressable
            p={3}
            borderBottomWidth={0.5}
            w="full"
            alignItems="center"
            borderBottomColor={Colors.deepestGray}
            onPress={() => setShow(false)}
          >
            <Text>Tin đã ẩn (1)</Text>
          </Pressable>
          <Pressable
            p={3}
            w="full"
            alignItems="center"
            onPress={() => setShow(false)}
          >
            <Text>Tin đợi duyệt (1)</Text>
          </Pressable>
        </View>
      )}
      <ScrollView showsVerticalScrollIndicator={false}>
        {product.length > 0 ? (
          product.map((product) => (
            <View
              key={product._id}
              mt={2}
              bg={Colors.white}
              flexDirection="row"
              alignItems="center"
            >
              <View w="30%" borderRightWidth={0.5} borderRightColor="gray.400">
                <Image
                  source={{
                    uri: product.image,
                  }}
                  alt="img"
                  h={20}
                  resizeMode="contain"
                />
              </View>

              <View ml={2} w="40%">
                {product.isAccept === 0 ? (
                  <Text color={Colors.red} bold>
                    Đợi duyệt
                  </Text>
                ) : (
                  ""
                )}
                <Text isTruncated>{product.title}</Text>
                <Text bold>
                  {currencyFormatter(product.price, defaultOptions)}đ
                </Text>
              </View>
              <Spacer />
              <Pressable
                borderWidth={0.8}
                mr={5}
                rounded={10}
                pr={4}
                pl={4}
                pt={1}
                pb={1}
              >
                <Text>{product.isShow ? "Ẩn tin" : "Hiện tin"}</Text>
              </Pressable>
            </View>
          ))
        ) : (
          <View flex={1} mt={20} alignItems="center">
            <Text fontSize={15}>Bạn chưa có tin nào trong mục này</Text>
          </View>
        )}
      </ScrollView>
    </Box>
  );
}

export default Other;
