import React from "react";
import {
  Box,
  HStack,
  Image,
  Pressable,
  ScrollView,
  Spacer,
  Text,
  View,
} from "native-base";
import Colors from "../../color";
import { FontAwesome } from "@expo/vector-icons";

function Reject() {
  return (
    <Box h="full" bg={Colors.gray}>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View mt={2} bg={Colors.white} flexDirection="row" alignItems="center">
          <View w="30%" borderRightWidth={0.5} borderRightColor="gray.400">
            <Image
              source={{
                uri: "https://thoitrangbigsize.vn/wp-content/uploads/2019/09/1-6.jpg",
              }}
              alt="img"
              h={20}
              resizeMode="contain"
            />
          </View>

          <View ml={2}>
            <Text>Áo khoác</Text>
            <Text bold>100,000đ</Text>
          </View>
          <Spacer />
          <Pressable
            borderWidth={0.8}
            mr={5}
            rounded={10}
            pr={4}
            pl={4}
            pt={1}
            pb={1}
          >
            <Text>Hiện tin</Text>
          </Pressable>
        </View>

        <View mt={2} bg={Colors.white} flexDirection="row" alignItems="center">
          <View w="30%" borderRightWidth={0.5} borderRightColor="gray.400">
            <Image
              source={{
                uri: "https://thoitrangbigsize.vn/wp-content/uploads/2019/09/1-6.jpg",
              }}
              alt="img"
              h={20}
              resizeMode="contain"
            />
          </View>

          <View ml={2}>
            <Text>Áo khoác</Text>
            <Text bold>100,000đ</Text>
          </View>
          <Spacer />
          <Pressable
            borderWidth={0.8}
            mr={5}
            rounded={10}
            pr={4}
            pl={4}
            pt={1}
            pb={1}
          >
            <Text>Đã huỷ</Text>
          </Pressable>
        </View>
      </ScrollView>
    </Box>
  );
}

export default Reject;
