import {
  Box,
  Text,
  ScrollView,
  HStack,
  Pressable,
  View,
  Image,
  VStack,
  Input,
} from "native-base";
import React from "react";
import Colors from "../color";
import { Ionicons } from "@expo/vector-icons";
import { TouchableOpacity } from "react-native";

function CreateBlogScreen({ navigation }) {
  return (
    <Box safeAreaTop bg={Colors.white}>
      {/* Header */}
      <HStack space={3} w="full" px={4} bg="#FFA500" py={3} alignItems="center">
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Đăng tin
        </Text>
      </HStack>
      {/* Body */}
      <ScrollView showsVerticalScrollIndicator={false} mt={5}>
        <View bg={Colors.white} w="full" px={3}>
          <Text fontSize={13} color="gray.500">
            Danh mục
          </Text>
          <Pressable
            mt={1}
            borderWidth={1}
            p={3}
            rounded={5}
            flexDirection="row"
            justifyContent="space-between"
            borderColor="gray.400"
            onPress={() => {
              navigation.back();
            }}
          >
            <Text>Xe cộ</Text>
            <Ionicons
              name="chevron-forward"
              size={20}
              color={Colors.deepestGray}
              style={{ alignSelf: "center" }}
            />
          </Pressable>
        </View>
        <VStack mt={5}>
          <Pressable
            px={3}
            h={10}
            borderTopWidth={0.5}
            borderTopColor="gray.400"
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
            flexDirection="row"
            alignItems="center"
            justifyContent="space-between"
          >
            <Text>Xe cộ</Text>
            <Ionicons
              name="chevron-forward"
              size={28}
              color={Colors.deepestGray}
              style={{ alignSelf: "center" }}
            />
          </Pressable>
          <Pressable
            px={3}
            h={10}
            borderTopWidth={0.5}
            borderTopColor="gray.400"
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
            flexDirection="row"
            alignItems="center"
            justifyContent="space-between"
          >
            <Text>Đồ điện tử</Text>
            <Ionicons
              name="chevron-forward"
              size={28}
              color={Colors.deepestGray}
              style={{ alignSelf: "center" }}
            />
          </Pressable>
          <Pressable
            px={3}
            h={10}
            borderTopWidth={0.5}
            borderTopColor="gray.400"
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
            flexDirection="row"
            alignItems="center"
            justifyContent="space-between"
          >
            <Text>Thời trang, đồ dùng cá nhân</Text>
            <Ionicons
              name="chevron-forward"
              size={28}
              color={Colors.deepestGray}
              style={{ alignSelf: "center" }}
            />
          </Pressable>
          <Pressable
            px={3}
            h={10}
            borderTopWidth={0.5}
            borderTopColor="gray.400"
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
            flexDirection="row"
            alignItems="center"
            justifyContent="space-between"
          >
            <Text>Giải trí thể thao</Text>
            <Ionicons
              name="chevron-forward"
              size={28}
              color={Colors.deepestGray}
              style={{ alignSelf: "center" }}
            />
          </Pressable>
          <Pressable
            px={3}
            h={10}
            borderTopWidth={0.5}
            borderTopColor="gray.400"
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
            flexDirection="row"
            alignItems="center"
            justifyContent="space-between"
          >
            <Text>Tủ lạnh, máy giặt,...</Text>
            <Ionicons
              name="chevron-forward"
              size={28}
              color={Colors.deepestGray}
              style={{ alignSelf: "center" }}
            />
          </Pressable>
          <Pressable
            px={3}
            h={10}
            borderTopWidth={0.5}
            borderTopColor="gray.400"
            borderBottomWidth={0.5}
            borderBottomColor="gray.400"
            _pressed={{ opacity: 0.5 }}
            flexDirection="row"
            alignItems="center"
            justifyContent="space-between"
          >
            <Text>Đồ gia dụng, nội thất</Text>
            <Ionicons
              name="chevron-forward"
              size={28}
              color={Colors.deepestGray}
              style={{ alignSelf: "center" }}
            />
          </Pressable>
        </VStack>
      </ScrollView>
    </Box>
  );
}

export default CreateBlogScreen;
