import React from "react";
import {
  Box,
  HStack,
  Image,
  Pressable,
  ScrollView,
  Spacer,
  Text,
  View,
} from "native-base";
import Colors from "../../color";
import { FontAwesome } from "@expo/vector-icons";

function Other() {
  return (
    <Box h="full" bg={Colors.gray}>
      <Pressable bg={Colors.white} p={2} alignItems="center" shadow={1}>
        <HStack>
          <Text fontSize={16}>Lọc tin</Text>
          <FontAwesome
            name="sort-down"
            size={16}
            color="black"
            style={{ marginLeft: 5 }}
          />
        </HStack>
      </Pressable>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View mt={2} bg={Colors.white}>
          <View flexDirection="row">
            <View w="30%" borderRightWidth={0.5} borderRightColor="gray.400">
              <Image
                source={{
                  uri: "https://thoitrangbigsize.vn/wp-content/uploads/2019/09/1-6.jpg",
                }}
                alt="img"
                h={20}
                resizeMode="contain"
              />
            </View>
            
            <View mt={2} ml={2}>
              <Text>Áo khoác</Text>
              <Text bold>100,000đ</Text>
            </View>
            <Spacer />
            <HStack justifyContent="space-between">
              <Text>âdada</Text>
              <Pressable borderWidth={0.8} ali>
                <Text>Hiện tin</Text>
              </Pressable>
            </HStack>
          </View>
        </View>
      </ScrollView>
    </Box>
  );
}

export default Other;
