import { useNavigation } from '@react-navigation/native';
import { Flex, ScrollView, Text, Image, Box, Heading ,Pressable} from 'native-base';

import React from 'react';
import { TouchableOpacity } from 'react-native';
import Colors from '../color';
import products from '../data/Products';
import Rating from './Rating';

function HomeProducts() {
    const navigation = useNavigation();
  return (
    <ScrollView flex={1} showsVerticalScrollIndicator={false} >
        <Flex flexWrap="wrap" direction="row" px={2}> 
            {products.map((product) => (
                <TouchableOpacity key={product._id} style={{ width: "50%", backgroundColor: Colors.white, borderStyle: 'solid'}} 
                onPress={()=>navigation.navigate('Single',product)}
                >
                    <Image source={{uri:product.image}} alt={product.name} w="full"
                        h={24} resizeMode="contain"/>
                        <Box px={4} pt={1}>
                            <Heading size="sm" bold>
                                ${product.price}
                            </Heading>
                            <Text fontSize={10} mt={1} isTruncated>
                                {product.name}
                            </Text>
                            <Rating value={product.rating}/>
                        </Box>
                </TouchableOpacity>       
            ))}
        </Flex>
    </ScrollView>
  );
}

export default HomeProducts;
