import React from 'react';
import { Box, Center, Pressable, HStack, Text } from 'native-base';
import Colors from '../color';
import {FontAwesome} from '@expo/vector-icons';
import { SwipeListView } from 'react-native-swipe-list-view';
import products from '../data/Products';

const Swiper = () => {
    <SwipeListView rightOpenValue={-50} previewRowKey={0} 
        previewOpenValue={-40} previewOpenDelay={3000} data={products.slice(0,2)}
        renderHiddenItem={renderHiddenItems} renderItem={renderItems} showsVerticalScrollIndicator={false}>

    </SwipeListView>
}

const renderItems = (data) => {
    <Pressable >
        <Box ml={6} mb={3}>
            <HStack alignItems="center" bg={Colors.white} 
                shadow={1} rounded={10} overflow="hidden">
                <Text>Item</Text>
            </HStack>
        </Box>
    </Pressable>
}

const renderHiddenItems = () => {
    <Pressable w={50} roundedTopRight={10} roundedBottomRight={10} h="88%" ml="auto" justifyContent="center" 
     bg={Colors.red}>
        <Center alignItems="center" space={2}>
            <HStack alignItems="center" bg={Colors.white} 
                shadow={1} rounded={10} overflow="hidden">
                <Text>Item</Text>
            </HStack>
        </Center>
    </Pressable>
}

function CartItem() {
  return (
    <Box mr={6}>
        <Swiper />
    </Box>
  );
}

export default CartItem;
