import React from "react";
import { View, Text, Box, ScrollView, Heading, Modal } from "native-base";
import Colors from "../color";
import OrderInfo from "../Components/OrderInfo";
import {FontAwesome,FontAwesome5,Ionicons} from "@expo/vector-icons";
import OrderItem from "../Components/OrderItem";
function PlaceOrderScreen() {
  return (
    <Box bg={Colors.subGreen} flex={1} safeAreaTop pt={6}>
    <ScrollView showsVerticalScrollIndicator={false}>
      <Box>
        <ScrollView showsHorizontalScrollIndicator={false} horizontal={true}>
          <OrderInfo
            title="CUSTOMER"
            subTitle="Admin joe"
            text="admin@gmail.com"
            icon={<FontAwesome name="user" size={30} color={Colors.white}></FontAwesome>}
          />
          <OrderInfo
            title="SHIPPING INFO"
            subTitle="Shipping: Mew"
            text="Pay method: Paypal"
            icon={<FontAwesome5 name="shipping-fast" size={30} color={Colors.white}></FontAwesome5>}
          />
          <OrderInfo
            title="DELIVER TO"
            subTitle="Address:"
            text="Da nang hoang dieu"
            icon={<Ionicons name="location-sharp" size={30} color={Colors.white}></Ionicons>}
          />
        </ScrollView>
      </Box>
      {/* ORDER ITEM */}
      <Box px={6} flex={1} pb={3}> 
        <Heading bold fontSize={15} isTruncated my={4}>
          PRODUCTS
        </Heading>
        <OrderItem/>
        {/* TOTAL */}
        <Box>
          <Text>Detail payment</Text>
        </Box>
      </Box>
      </ScrollView>
    </Box>
  );
}

export default PlaceOrderScreen;
